import axios from 'axios';
import React from 'react';
import { useState } from 'react';
import {useNavigate} from 'react-router'
const AdminLogin = () => {
let navigate = useNavigate();

const [username, setUsername]=useState('');
const [password, setPassword]=useState('');
function textchanged(e){
    e.preventDefault();
    if(e.target.id === "username")
    {
      setUsername(e.target.value);
    }
    if(e.target.id === "password")
    {
      setPassword(e.target.value);
    }
  }
  function login(e){
    e.preventDefault();
    let data = {data:{username:username, password:password}};
    console.log(data);

    axios.post("http://localhost:8081/admin/login", data).then((result)=>{
      if(result.data.data.status === "success"){
        localStorage.setItem("usertype", "admin");
        navigate('/administration');  
      }
      else{
        alert("Invalid credentials");  
      }

    })
  }


    return(
        <>
        
        <div class="wrapper fadeInDown">
        <div id="formContent">
        
            <div class="fadeIn first">
                <img src="https://cdn-icons-png.flaticon.com/512/5087/5087579.png" id="icon" alt="User Icon" />
            </div>
            <input type="text" id="email" class="fadeIn second" placeholder="email" />
            <input type="text" id="password" class="fadeIn third" placeholder="password"/>
            <input type="submit" onClick={(e)=>{login(e)}} class="fadeIn fourth" value="Log In" onclick="login()"/>            
            <div id="formFooter">
                <a class="underlineHover" href="#">Forgot Password?</a>
            </div>

        </div>
    </div>
        </>
        
    )
}

export default Login;
