import axios from 'axios';
import React, { useEffect, useState } from 'react'
import {Link, useNavigate, useParams,} from 'react-router-dom'
function Category(){
  const navigate = useNavigate();
  const [data, setData]= useState({
    id:'',
    name:'',
    srno:'',
    image:'',
  })
  let { id } = useParams();
  useEffect(()=>{
    if(id !== null)
    {
      axios.post('http://localhost:8081/productcategory/get/', {data:{id:id}})
        .then((response) => {
          const newData = {...data};
          newData["id"] = response.data.data._id;
          setData(newData);
        })
    }
  }, []);
  function handle (e){       
        const newData = {...data};
        newData[e.target.id] = e.target.value;
        setData(newData);
    }

function submit(e){
  e.preventDefault();
  axios.post("http://localhost:8081/productcategory/save",{data:{
      id: data.id === null ? "" : data.id,
      name: data.name,
      image : data.image,
      srno: data.srno
  }}).then(res=>{
      navigate('/administration/categories');
  })
}

    return(

        <div>
     <hr class='container'/>
       <div class="breadcrumbs">
		<p class="bread"><span><Link to="/administration">Admin</Link></span> / <span> <Link to="/administration/productcategories"> Product Categories </Link></span>/<span> Product Category</span></p>
        <h1 class='text-center bg-light'>Add Category</h1>
		</div>
     
 <div class="container">
      <div class="row">
        <div class="col-xxl-8 col-10 col-md-8 mx-auto">
          <form action="">
            
            
            <div class="mb-3">
            <label for="srno">Sr No</label>
            <input class="form-control" type="number" min="1"  value={data.srno} id="srno" onChange={(e)=>handle(e)}  id="srno" />
            </div>

            <div class="mb-3">
              <label for="exampleInputPassword1" class="form-label">Category Name</label>
              <input type="text" class="form-control" value={data.name} onChange={(e)=>{handle(e)}} id="name" placeholder="Enter Category name"
                name="name"/>
            </div>

            <div class="mb-3">
            <label for="image">Image</label>
            <input type="fil"  class="form-control"  onChange={(e)=>handle(e)} id="image" />
            <button type="submit" class="btn btn-primary"  onClick={(e)=>{submit(e)}}>Save</button>
            </div>
          </form>
        </div>
      </div>
    </div>


    </div>
    )
}
export default Category;
